#!/usr/bin/env node
'use strict';
var meow = require('meow');
var wlan = require('./');

var cli = meow({
	help: [
		'Usage',
		'  $ wlan [input]',
		'',
		'Examples',
		'  $ wlan',
		'  unicorns & rainbows',
		'',
		'  $ wlan ponies',
		'  ponies & rainbows',
		'',
		'Options',
		'  --foo  Lorem ipsum. Default: false'
	]
});

console.log(wlan(cli.input[0] || 'unicorns'));
